import Account from "../components/account";
import { store } from "../store";
import { useDispatch, useSelector } from "react-redux";
import React, { useState } from "react";
import { userName } from "../redux/username";

const User = () => {
  const accountData = [
    {
      title: "Argent Bank Checking (x8349)",
      content: `$2.082.79`,
      description: "Available Balance",
    },
    {
      title: "Argent Bank Savings (x6712)",
      content: `$10.928.42`,
      description: "Available Balance",
    },
    {
      title: "Argent Bank Credit Card (x8349)",
      content: `$184.30`,
      description: "Current Balance",
    },
  ];
  const user = useSelector((state) => state.user);
  const [isEditing, setIsEditing] = useState(false);
  const [newUserName, setNewUserName] = useState("");
  const oldUserName = user.userName;
  const dispatch = useDispatch();
  const handleEditClick = () => {
    setIsEditing(!isEditing);
  };
  const handleSubmitClick = () => {
    dispatch(userName(newUserName));
  };

  const tag =
    store.getState().user.firstName + " " + store.getState().user.lastName;

  return (
    <main className="main bg-dark">
      <div className="header">
        <div className="profile">
          {isEditing ? (
            <div className="userInfo">
              <h1>Edit user info</h1>
              <div className="edit">
                <div className="edit__form">
                  <h3>Username: </h3>
                  <input
                    className="edit__form__input"
                    type="text"
                    placeholder={oldUserName}
                    onChange={(e) => setNewUserName(e.target.value)}
                  />
                </div>
                <div className="edit__form">
                  <h3>First name: </h3>
                  <input
                    className="edit__form__input locked"
                    type="text"
                    defaultValue={user.firstName}
                    disabled
                  />
                </div>
                <div className="edit__form">
                  <h3>Last name: </h3>
                  <input
                    className="edit__form__input locked"
                    type="text"
                    defaultValue={user.lastName}
                    disabled
                  />
                </div>
              </div>
              <div className="editButtons">
                <button
                  className="profile__button saveClose"
                  onClick={handleSubmitClick}
                >
                  Save
                </button>
                <button
                  className="profile__button saveClose"
                  onClick={handleEditClick}
                >
                  Close
                </button>
              </div>
            </div>
          ) : (
            <div>
              {store.getState().user.firstName === null ? (
                <img
                  src="https://i.gifer.com/ZKZg.gif"
                  style={{ padding: 40 }}
                  width={100}
                />
              ) : (
                <h1 style={{ padding: 10 }}>
                  Welcome back
                  <br />
                  {tag} !
                </h1>
              )}
              <button className="edit-button" onClick={handleEditClick}>
                Edit Name
              </button>
            </div>
          )}
        </div>
      </div>
      {accountData.map(({ title, content, description }) => (
        <Account
          key={title}
          title={title}
          content={content}
          description={description}
        />
      ))}
    </main>
  );
};

export default User;
