import store from "../store";
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Navigate } from 'react-router-dom';



export const fetchProfile = async (userInfo) => {
  try {
    const rawResponse = await fetch("http://localhost:3001/api/v1/user/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: userInfo.email,
        password: userInfo.password,
      }),
    });

    const content = await rawResponse.json();
if (content.status === 200){
    return <Navigate replace to="/account"/>;
}
    console.log("CONTENT ==> ", content);
  } catch (error) {
    console.log(error);
    console.error(
      "Cet identifiant ou ce mot de passe est inconnu, veuillez réessayer."
    );
  }
};

const Form = () => {
  const isLogged = useSelector((state) => state.login.isLogged);
  const dispatch = useDispatch();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  return (
    <div>
      <div className="input-wrapper">
        <label htmlFor="username">Username</label>
        <input
          onChange={(e) => setEmail(e.target.value)}
          type="text"
          id="username"
        />
      </div>
      <div className="input-wrapper">
        <label htmlFor="password">Password</label>
        <input
          onChange={(e) => setPassword(e.target.value)}
          type="password"
          id="password"
        />
      </div>
      <div className="input-remember">
        <input type="checkbox" id="remember-me" />
        <label htmlFor="remember-me">Remember me</label>
      </div>
      {/* <a href="./user.html" className="sign-in-button">Sign In</a> */}
      <button
        className="sign-in-button"
        onClick={() => fetchProfile({ email, password })}
      >
        Sign In
      </button>
    </div>
  );
};

export default Form;
