import React from "react";
import Logo from "../assets/argentBankLogo.png";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { logout } from "../redux/login.logout";
import { useDispatch, useSelector } from "react-redux";
import { store } from "../store";
import Rightbracket from "../assets/Rightbracket.png";
import Circleuser from "../assets/Circleuser.png";

const Header = () => {
  const dispatch = useDispatch();
  const lienActif = useLocation();
  const isAuthenticated = useSelector((state) => state.isAuthenticated);
  const userName = useSelector((state) => state.user.userName);
  const navigate = useNavigate();

  const handleLogout = () => {
    dispatch(logout());
    navigate("/");
  };

  const handleProfile = () => {
    navigate("/user");
  };
  return (
    <nav className="main-nav">
      <Link
        to="/"
        className={`main-nav-logo ${lienActif.pathname === "/" ? "actif" : ""}`}
      >
        <img
          className="main-nav-logo-image"
          src={Logo}
          alt="Argent Bank Logo"
        />
      </Link>
      <div className="nav-items">
        <div className="main-nav-item">
          {store.getState().token === null ? (
            <Link
              to="/login"
              className={`main-nav-item ${
                lienActif.pathname === "/login" ? "actif" : ""
              }`}
            >
              <Circle />
              Sign In
            </Link>
          ) : (
            ""
          )}
        </div>

        {isAuthenticated && (
          <div className="main-nav-item" onClick={handleProfile}>
            <img src={Circleuser} className="circleuser" alt="circleuser" />
            <p>{userName}</p>
          </div>
        )}

        {isAuthenticated && (
          <div className="main-nav-item" onClick={handleLogout}>
            <img
              src={Rightbracket}
              className="rightbracket"
              alt="rightbracket"
            />
            <a>Sign Out</a>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Header;
