import store from '../store';
import { useEffect } from "react";
import { useSelector, useDispatch } from 'react-redux'
import { changeIsLogged } from '../slice'

export const Profil = (token) => {
    return async (dispatch) => {
        dispatch(fetchProfileStarted());
        try {
            const res = await axios.post(
                "http://localhost:3001/api/v1/user/profile", {},
                {
                    headers : {
                        accept : "application/json",
                        Authorization : token,
                    },
                }
            );
            dispatch(fetchProfileSucceeded(res.data));
        } catch (error) {
            dispatch(fetchProfileFailed(error));
        }
        };
    };


const Form = () => {
    const isLogged = useSelector((state) => state.login.isLogged)
    const dispatch = useDispatch();

    useEffect(() => {
        console.log('isLogged is ==> ', isLogged);
    }, []);

    return (
        <form>
        <div className="input-wrapper">
            <label htmlFor="username">Username</label><input type="text" id="username" />
        </div>
        <div className="input-wrapper">
            <label htmlFor="password">Password</label><input type="password" id="password" />
        </div>
        <div className="input-remember">
            <input type="checkbox" id="remember-me" /><label htmlFor="remember-me"
            >Remember me</label>
        </div>
        {/* <a href="./user.html" className="sign-in-button">Sign In</a> */}
        <button className="sign-in-button" onClick={() => changeIsLogged()} >Sign In</button>
        <h1>IS LOGGED = {isLogged.toString()}</h1>
    </form>
    );
};

export default Form;
