import React from "react";
import Logo from '../assets/argentBankLogo.png';
import { ReactComponent as Circle } from "../assets/circle-user.svg";
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { logout } from '../auth.service';
import { useDispatch, useSelector} from 'react-redux';

const Header = () => {
  const dispatch = useDispatch();
  const lienActif = useLocation();
  const isAuthenticated = useSelector(state => state.isAuthenticated);
  const navigate = useNavigate();

  const sign = store.getState().user.token + ' ' + store.getState().user.token;
  const handleLogout = () => {
    dispatch(logout());
    navigate('/');
};
  return (
    <nav className="main-nav">

<Link to="/" className={`main-nav-logo ${lienActif.pathname === '/' ? 'actif' : ''}`}>
        <img
          className="main-nav-logo-image"
          src={Logo}
          alt="Argent Bank Logo"
        />
        {/* <h1 className="sr-only">Argent Bank</h1> */}
      </Link>
      <div>
        
      <Link to="/login" className={`main-nav-item ${lienActif.pathname === '/login' ? 'actif' : ''}`}>
          <Circle />
          Sign In
        </Link>
      </div>
      {isAuthenticated && (
                    <div className='nav_button signout' onClick={handleLogout}>
                        <i className="fa fa-sign-out"></i>
                        <p>Sign Out</p>
                    </div>
                )}
    </nav>
  );
};

export default Header;
