const initialState = {
    firstName : null,
    lastName : null,
};

const profilReducer = (state = initialState, action) => {
    if (action.type === "GET_USER_DATA") {
        return {
            ...state,
            firstName: action.payload.firstName,
            lastName: action.payload.lastName,
        };
    }
    return state;
}
export default profilReducer;
