import React from "react";
import Logo from "../assets/argentBankLogo.png";
import { ReactComponent as Circle } from "../assets/circle-user.svg";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { logout } from "../auth.service";
import { useDispatch, useSelector } from "react-redux";
import { store } from "../store";
import { ReactComponent as RightBracket } from "../assets/Rightbracket.svg";
import { ReactComponent as Circleuser } from "../assets/Circleuser.svg";

const Header = () => {
  const dispatch = useDispatch();
  const lienActif = useLocation();
  const isAuthenticated = useSelector((state) => state.isAuthenticated);
  const userName = useSelector(state => state.user.firstName);
  const navigate = useNavigate();

  const handleLogout = () => {
    dispatch(logout());
    navigate("/");
  };
  return (
    <nav className="main-nav">
      <Link
        to="/"
        className={`main-nav-logo ${lienActif.pathname === "/" ? "actif" : ""}`}
      >
        <img
          className="main-nav-logo-image"
          src={Logo}
          alt="Argent Bank Logo"
        />
        {/* <h1 className="sr-only">Argent Bank</h1> */}
      </Link>
      <div className="main-nav-item">
        {store.getState().token === null ? (
          <Link
            to="/login"
            className={`main-nav-item ${
              lienActif.pathname === "/login" ? "actif" : ""
            }`}
          >
            <Circle />
            Sign In
          </Link>
        ) : (
          ""
        )}
      </div>
      <div>
      {isAuthenticated && (
                    <Link to={`/user`} >
                        <Circleuser/>
                        <p>{userName}</p>
                    </Link>
                )}
      </div>
      {isAuthenticated && (
        <div className="main-nav-item" onClick={handleLogout}>
          <RightBracket />
          <a>Sign Out</a>
        </div>
      )}
    </nav>
  );
};

export default Header;
