import store from '../store';
import {useSelector} from 'react-redux';
import { useEffect } from "react";


export const Profil = (token) => {
    return async (dispatch) => {
        dispatch(fetchProfileStarted());
        try {
            const res = await axios.post(
                "http://localhost:3001/api/v1/user/profile", {},
                {
                    headers : {
                        accept : "application/json",
                        Authorization : token,
                    },
                }
            );
            dispatch(fetchProfileSucceeded(res.data));
        } catch (error) {
            dispatch(fetchProfileFailed(error));
        }
        };
    };


const Form = () => {
    return (
        <form>
        <div className="input-wrapper">
            <label htmlFor="username">Username</label><input type="text" id="username" required />
        </div>
        <div className="input-wrapper">
            <label htmlFor="password">Password</label><input type="password" id="password" required />
        </div>
        <div className="input-remember">
            <input type="checkbox" id="remember-me" /><label htmlFor="remember-me"
            >Remember me</label>
        </div>
        {/* <a href="./user.html" className="sign-in-button">Sign In</a> */}
        <button className="sign-in-button" onClick={connexion}>Sign In</button>
    </form>
    );
};

export default Form;
